package examples.study6;import java.io.FileInputStream;import java.io.FileOutputStream;public class FileOutputStreamExample {    public static void main(String[] args) throws Exception {        String orginalFileName = "C:/study/space.jpg";        String targetFileName = "C:/study/temp/space.jpg";        FileInputStream fis = new FileInputStream(orginalFileName);        FileOutputStream fos = new FileOutputStream(targetFileName);        int readByteNo = 0;        byte[] readBytes = new byte[100];        while ((readByteNo = fis.read(readBytes)) != -1) {            fos.write(readBytes, 0, readByteNo);        }        fos.flush();        fos.close();        fis.close();        System.out.println("복사 성공");    }}