package examples.study6;import java.io.FileInputStream;import java.io.IOException;public class FileInputStreamExample {    public static void main(String[] args) {        FileInputStream fis = null;        try {            fis = new FileInputStream("C:\\study\\intelliJ_projects\\StudyJava\\src\\examples\\study6\\FileInputStreamExample.java");            int data = 0;            while ((data = fis.read()) != -1) {                System.out.write(data);            }        } catch (Exception e) {            e.printStackTrace();        } finally {            try {                fis.close();            } catch (IOException e) {                e.printStackTrace();            }        }    }}